//Common plugins
if (document.querySelectorAll("[toast-list]").length > 0 || document.querySelectorAll('[data-choices]').length > 0 || document.querySelectorAll("[data-provider]").length > 0) {
    var toastifyScript = document.createElement('script');
    toastifyScript.src = 'https://cdn.jsdelivr.net/npm/toastify-js';
    document.body.appendChild(toastifyScript);

    var choicesScript = document.createElement('script');
    choicesScript.src = '/../assets/libs/choices.js/choices.js.min.js';
    document.body.appendChild(choicesScript);

    var flatpickrScript = document.createElement('script');
    flatpickrScript.src = '/../assets/libs/flatpickr/flatpickr.min.js';
    document.body.appendChild(flatpickrScript);
}
